import { useEffect, useState } from "react";
import { Box, Typography, Card, CardContent } from "@mui/material";
import { Bar, Pie } from "react-chartjs-2";
import API from "../api/axios";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Tooltip,
  Legend
);

export default function Dashboard() {
  const [categoryData, setCategoryData] = useState([]);
  const [statusData, setStatusData] = useState([]);

  useEffect(() => {
    API.get("/analytics/category/").then(res => setCategoryData(res.data));
    API.get("/analytics/status/").then(res => setStatusData(res.data));
  }, []);

  const categoryChart = {
    labels: categoryData.map(item => item.category),
    datasets: [
      {
        label: "Complaints",
        data: categoryData.map(item => item.count),
      },
    ],
  };

  const statusChart = {
    labels: statusData.map(item => item.status),
    datasets: [
      {
        data: statusData.map(item => item.count),
      },
    ],
  };

  return (
    <Box p={2}>
      <Typography variant="h5" fontWeight={600} mb={2}>
        Analytics Dashboard
      </Typography>

      <Card sx={{ mb: 3, borderRadius: 3 }}>
        <CardContent>
          <Typography mb={1}>Complaints by Category</Typography>
          <Bar data={categoryChart} />
        </CardContent>
      </Card>

      <Card sx={{ borderRadius: 3 }}>
        <CardContent>
          <Typography mb={1}>Complaints by Status</Typography>
          <Pie data={statusChart} />
        </CardContent>
      </Card>
    </Box>
  );
}
