import {
  Card,
  CardContent,
  Typography,
  Box,
  IconButton,
  Button
} from "@mui/material";
import ChevronRightIcon from "@mui/icons-material/ChevronRight";
import StatusChip from "./StatusChip";
import API from "../api/axios"; // import your Axios instance

export default function ComplaintCard({ complaint, onClick }) {
  const handleStatusChange = (newStatus) => {
    API.patch(`/complaints/update-status/${complaint.id}/`, { status: newStatus })
      .then(res => {
        alert(`Status updated to ${res.data.status}`);
        window.location.reload(); // reload to show updated list
      })
      .catch(err => console.log(err));
  };

  return (
    <Card
      sx={{
        mb: 1.5,
        borderRadius: 3,
        boxShadow: "0 4px 10px rgba(0,0,0,0.05)",
        "&:hover": { boxShadow: "0 6px 15px rgba(0,0,0,0.1)", cursor: "pointer" },
      }}
      onClick={onClick}
    >
      <CardContent>
        <Box display="flex" justifyContent="space-between">
          <StatusChip label={complaint.priority} type="priority" />
          <StatusChip label={complaint.status} type="status" />
        </Box>

        <Typography variant="h6" mt={1}>
          {complaint.category}
        </Typography>

        <Typography variant="body2" color="text.secondary">
          {complaint.description}
        </Typography>

        {/* Buttons for updating status */}
        <Box mt={1} display="flex" gap={1}>
          <Button
            size="small"
            variant="contained"
            color="success"
            onClick={(e) => { e.stopPropagation(); handleStatusChange("Done"); }}
          >
            Mark as Done
          </Button>
          <Button
            size="small"
            variant="contained"
            color="warning"
            onClick={(e) => { e.stopPropagation(); handleStatusChange("Pending"); }}
          >
            Mark as Pending
          </Button>
        </Box>

        <Box
          mt={1.5}
          display="flex"
          justifyContent="space-between"
          alignItems="center"
        >
          <Typography variant="caption">
            üìç {complaint.location}
          </Typography>
          <IconButton size="small">
            <ChevronRightIcon />
          </IconButton>
        </Box>
      </CardContent>
    </Card>
  );
}
